import React from 'react';
import { FlatList, Image, Text, TouchableOpacity, View, ScrollView } from 'react-native';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';
import FeatherIcon from 'react-native-vector-icons/Feather';
import { connect } from 'react-redux';
import MowContainer from '../../components/ui/Core/Container/MowContainer';
import { MowInput } from '../../components/ui/Common/Input/MowInput';
import { MowButtonBasic } from '../../components/ui/Common/Button/MowButton';
import { MowModal } from '../../components/ui/Common/Modal/MowModal';
import { mowColors } from '../../values/Colors/MowColors';
import { cardStyle, pageContainerStyle } from '../../values/Styles/MowStyles';
import { TrackOrder } from './trackOrder.component';
import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scroll-view';

interface IProps {
    navigation: any;
    route: any;
    order: any;
}

interface IState {
    isTrackViewVisible: boolean;
    isCancelRequestModalVisible: boolean;
    reason: any;
    description: any;
}

class OrderDetailsScreen extends React.Component<IProps, IState> {

    orderOperationsStyle = {
        container: {
            flexDirection: 'row',
            marginTop: hp('2.5%'),
            alignItems: 'center',
        },
        containerInfo: {
            flexDirection: 'row',
            alignItems: 'center',
            width: '100%',
        },
        icon: {
            color: mowColors.mainColor,
            fontSize: hp('3'),
        },
        title: {
            marginLeft: 10,
            fontSize: hp('1.5'),
            fontWeight: 'normal',
            fontStyle: 'normal',
            letterSpacing: 0,
            textAlign: 'center',
            color: mowColors.titleTextColor,
        },
        sectionTitle: {
            marginLeft: 10,
            fontSize: hp('1.8'),
            fontWeight: '600',
            fontStyle: 'normal',
            letterSpacing: 0,
            textAlign: 'center',
            color: mowColors.titleTextColor,
        },
    };

    inputStyle = {
        container: {
            backgroundColor: mowColors.viewBGColor,
        },
    };

    constructor(props) {
        super(props);
        this.state = {
            isTrackViewVisible: false,
            isCancelRequestModalVisible: false,
            reason: null,
            description: null,
        }
    }

    toggleTrackView = () => {
        const { isTrackViewVisible } = this.state;
        this.setState({ isTrackViewVisible: !isTrackViewVisible });
    }

    onModalClose = () => {
        this.setState({ isCancelRequestModalVisible: false });
    }

    onChangeText = (name, value) => {
        if (name === 'reason') {
            this.setState({ reason: value });
        }

        if (name === 'description') {
            this.setState({ description: value });
        }
    }

    submit = () => {
        this.setState({ isCancelRequestModalVisible: false });
    }

    render() {
        return (
            <MowContainer
                navigation={this.props.navigation}
                title={this.props.order?.selectedOrder?.orderCode}>
                <ScrollView>
                    <View
                        style={pageContainerStyle}>
                        <View
                            style={{ padding: 15, ...cardStyle, backgroundColor: mowColors.categoryBGColor }}>
                            <FlatList
                                showsVerticalScrollIndicator={false}
                                keyExtractor={(item, index) => index.toString()}
                                data={this.props.order.selectedOrder.orderDetails}
                                renderItem={({ item, index }) => (
                                    <View
                                        style={{ flexDirection: 'row', width: '100%' }}>
                                        <View
                                            style={{ alignItems: 'center', justifyContent: 'center' }}>
                                            <Image
                                                source={{ uri: item.product.featuredImage }}
                                                resizeMode={'contain'}
                                                style={{ height: hp('12%'), width: hp('12%'), borderRadius: 10 }} />
                                        </View>
                                        <View
                                            style={{ marginLeft: 10 }}>
                                            <Text
                                                style={{
                                                    fontSize: hp('1.7%'),
                                                    fontWeight: 'normal',
                                                    fontStyle: 'normal',
                                                    letterSpacing: 0,
                                                    textAlign: 'left',
                                                    color: mowColors.titleTextColor,
                                                }}>
                                                {item.product.name}
                                            </Text>
                                            <Text
                                                style={{
                                                    marginVertical: 10,
                                                    fontSize: hp('1.8%'),
                                                    fontWeight: 'bold',
                                                    fontStyle: 'normal',
                                                    letterSpacing: 0,
                                                    textAlign: 'left',
                                                    color: mowColors.textColor,
                                                }}>
                                                Quantity: {item.quantity}
                                            </Text>
                                        </View>
                                    </View>
                                )}
                            />

                            {
                                this.props.order.selectedOrder.orderStatus !== 'canceled' ?
                                    <View
                                        style={{ width: '100%' }}>
                                        <TouchableOpacity
                                            onPress={this.toggleTrackView}
                                            style={
                                                {
                                                    flexDirection: 'row',
                                                    marginTop: hp('2.5%'),
                                                    alignItems: 'center'
                                                }
                                            }>
                                            <FeatherIcon
                                                name={'box'}
                                                style={this.orderOperationsStyle.icon} />

                                            <Text
                                                style={{
                                                    marginLeft: 10,
                                                    fontSize: hp('1.5'),
                                                    fontWeight: 'normal',
                                                    fontStyle: 'normal',
                                                    letterSpacing: 0,
                                                    textAlign: 'center',
                                                    color: mowColors.titleTextColor,
                                                }}>
                                                Track Order Status
                                        </Text>
                                        </TouchableOpacity>
                                        {
                                            this.state.isTrackViewVisible ?
                                                <TrackOrder data={this.props.order.selectedOrder.orderStatus} /> : null
                                        }
                                        <TouchableOpacity
                                            onPress={() => { this.setState({ isCancelRequestModalVisible: true }) }}
                                            style={{
                                                flexDirection: 'row',
                                                marginTop: hp('2.5%'),
                                                alignItems: 'center',
                                            }}>
                                            <FeatherIcon
                                                name={'x-circle'}
                                                style={this.orderOperationsStyle.icon} />

                                            <Text
                                                style={{
                                                    marginLeft: 10,
                                                    fontSize: hp('1.5'),
                                                    fontWeight: 'normal',
                                                    fontStyle: 'normal',
                                                    letterSpacing: 0,
                                                    textAlign: 'center',
                                                    color: mowColors.titleTextColor,
                                                }}>
                                                Request For Cancellation
                                        </Text>
                                        </TouchableOpacity>
                                    </View> : null
                            }

                        </View>
                        <View>
                            <View
                                style={{
                                    padding: 15,
                                    ...cardStyle,
                                    marginTop: 15,
                                    backgroundColor: mowColors.categoryBGColor,
                                }}>
                                <View
                                    style={{
                                        flexDirection: 'row',
                                        alignItems: 'center',
                                        width: '100%',
                                    }}>
                                    <FeatherIcon
                                        name={'box'}
                                        style={this.orderOperationsStyle.icon} />
                                    <Text
                                        style={{
                                            marginLeft: 10,
                                            fontSize: hp('1.8'),
                                            fontWeight: '600',
                                            fontStyle: 'normal',
                                            letterSpacing: 0,
                                            textAlign: 'center',
                                            color: mowColors.titleTextColor,
                                        }}>
                                        Payment Details
                                </Text>
                                </View>
                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    Sub Total: {this.props.order.selectedOrder.orderPaymentDetails.subTotal}
                                </Text>
                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    Vat: {this.props.order.selectedOrder.orderPaymentDetails.vatTotal}
                                </Text>
                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    Coupon Discount: {this.props.order.selectedOrder.orderPaymentDetails.couponDiscountTotal}
                                </Text>
                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    Promotion Discount: {this.props.order.selectedOrder.orderPaymentDetails.promotionalDiscountTotal}
                                </Text>
                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    Total: {this.props.order.selectedOrder.orderPaymentDetails.total}
                                </Text>
                            </View>
                        </View>
                        <View>
                            <View
                                style={{
                                    padding: 15,
                                    ...cardStyle,
                                    marginTop: 15,
                                    backgroundColor: mowColors.categoryBGColor,
                                }}>
                                <View
                                    style={{
                                        flexDirection: 'row',
                                        alignItems: 'center',
                                        width: '100%',
                                    }}>
                                    <FeatherIcon
                                        name={'box'}
                                        style={this.orderOperationsStyle.icon} />
                                    <Text
                                        style={{
                                            marginLeft: 10,
                                            fontSize: hp('1.8'),
                                            fontWeight: '600',
                                            fontStyle: 'normal',
                                            letterSpacing: 0,
                                            textAlign: 'center',
                                            color: mowColors.titleTextColor,
                                        }}>
                                        Billing Address
                                </Text>
                                </View>

                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.billingInfo.firstName} {this.props.order.selectedOrder.billingInfo.lastName}
                                </Text>

                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.billingInfo.phoneNumber}
                                </Text>

                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.billingInfo.address}
                                </Text>

                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.billingInfo.city}
                                </Text>

                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.billingInfo.zip}
                                </Text>
                            </View>
                        </View>
                        <View>
                            <View
                                style={{
                                    padding: 15,
                                    ...cardStyle,
                                    marginTop: 15,
                                    backgroundColor: mowColors.categoryBGColor,
                                }}>
                                <View
                                    style={{
                                        flexDirection: 'row',
                                        alignItems: 'center',
                                        width: '100%',
                                    }}>
                                    <FeatherIcon
                                        name={'box'}
                                        style={this.orderOperationsStyle.icon} />
                                    <Text
                                        style={{
                                            marginLeft: 10,
                                            fontSize: hp('1.8'),
                                            fontWeight: '600',
                                            fontStyle: 'normal',
                                            letterSpacing: 0,
                                            textAlign: 'center',
                                            color: mowColors.titleTextColor,
                                        }}>
                                        Shipping Address
                                </Text>
                                </View>

                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.shippingInfo.firstName} {this.props.order.selectedOrder.shippingInfo.lastName}
                                </Text>

                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.shippingInfo.phoneNumber}
                                </Text>

                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.shippingInfo.address}
                                </Text>

                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.shippingInfo.city}
                                </Text>

                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.shippingInfo.zip}
                                </Text>
                            </View>
                        </View>
                        <View>
                            <View
                                style={{
                                    padding: 15,
                                    ...cardStyle,
                                    marginTop: 15,
                                    backgroundColor: mowColors.categoryBGColor,
                                }}>
                                <View
                                    style={{
                                        flexDirection: 'row',
                                        alignItems: 'center',
                                        width: '100%',
                                    }}>
                                    <FeatherIcon
                                        name={'box'}
                                        style={this.orderOperationsStyle.icon} />
                                    <Text
                                        style={{
                                            marginLeft: 10,
                                            fontSize: hp('1.8'),
                                            fontWeight: '600',
                                            fontStyle: 'normal',
                                            letterSpacing: 0,
                                            textAlign: 'center',
                                            color: mowColors.titleTextColor,
                                        }}>
                                        Preferred Delivery Timing
                                </Text>
                                </View>
                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.orderOptions.deliveryTiming}
                                </Text>
                            </View>
                        </View>
                        <View>
                            <View
                                style={{
                                    padding: 15,
                                    ...cardStyle,
                                    marginTop: 15,
                                    backgroundColor: mowColors.categoryBGColor,
                                }}>
                                <View
                                    style={{
                                        flexDirection: 'row',
                                        alignItems: 'center',
                                        width: '100%',
                                    }}>
                                    <FeatherIcon
                                        name={'box'}
                                        style={this.orderOperationsStyle.icon} />
                                    <Text
                                        style={{
                                            marginLeft: 10,
                                            fontSize: hp('1.8'),
                                            fontWeight: '600',
                                            fontStyle: 'normal',
                                            letterSpacing: 0,
                                            textAlign: 'center',
                                            color: mowColors.titleTextColor,
                                        }}>
                                        Payment Method
                                </Text>
                                </View>
                                <Text
                                    style={{
                                        width: '100%',
                                        fontSize: hp('1.6'),
                                        fontWeight: 'normal',
                                        fontStyle: 'normal',
                                        letterSpacing: 0,
                                        lineHeight: 25,
                                        textAlign: 'left',
                                        color: mowColors.titleTextColor,
                                        marginTop: 10,
                                        marginLeft: 5,
                                    }}>
                                    {this.props.order.selectedOrder.orderOptions.paymentMethod}
                                </Text>
                            </View>
                        </View>
                    </View>
                </ScrollView>

                <MowModal
                    modalVisible={this.state.isCancelRequestModalVisible}
                    onClosed={this.onModalClose}
                    title={'Order Cancel Request'}
                    closeButton={true}>

                    <KeyboardAwareScrollView
                        style={pageContainerStyle}
                        showsVerticalScrollIndicator={false}>

                        <MowInput
                            containerStyle={this.inputStyle.container}
                            onChangeText={value => this.onChangeText('reason', value)}
                            placeholder={'Reason'}
                            type={'text'} />

                        <MowInput
                            containerStyle={this.inputStyle.container}
                            onChangeText={value => this.onChangeText('description', value)}
                            placeholder={'Describe Elaborately'}
                            type={'textarea'} />

                    </KeyboardAwareScrollView>
                    <View
                        style={{ marginHorizontal: wp('3%') }}>
                        <MowButtonBasic
                            disabled={
                                this.state.reason === null ||
                                this.state.description === null
                            }
                            onPress={this.submit}
                            size={'medium'}
                            type={'success'}>
                            Submit
                        </MowButtonBasic>
                    </View>
                </MowModal>

            </MowContainer>
        );
    }
}

const mapStateToProps = (state) => {
    const { order } = state;
    return { order };
};
// const mapDispatchToProps = {
//     setSelectedOrder: setSelectedOrder,
// };
export default connect(mapStateToProps, null)(OrderDetailsScreen);
