import React from 'react';
import { FlatList, Image, View } from 'react-native';
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';
import { mowColors } from '../../../values/Colors/MowColors';
import { categoryStyle } from '../../../values/Styles/MowStyles';

interface IProps {
    navigation: any;
    data: any;
}

export const TopCollection = ({ navigation, data }): React.ReactElement => {

    return (
        <View
            style={[categoryStyle, { marginTop: 15, backgroundColor: mowColors.categoryBGColor }]}>

            <FlatList
                horizontal={true}
                showsHorizontalScrollIndicator={false}
                keyExtractor={(item, index) => index.toString()}
                data={data}
                renderItem={({ item, index }) => (
                    <View
                        style={{
                            width: wp('65%'),
                            height: hp('33%'),
                            marginHorizontal: 10,
                        }}
                        key={index}>
                        <View
                            style={{
                                height: '80%',
                                width: '100%',
                                borderRadius: 10,
                                borderStyle: 'solid',
                                borderWidth: 1,
                                borderColor: 'rgba(112, 112, 112, 0.16)',
                            }}>

                            <Image
                                style={{
                                    height: '100%',
                                    width: '100%',
                                }}
                                resizeMode={'stretch'}
                                source={{ uri: item.image }} />

                        </View>
                    </View>
                )}
            />

        </View>
    )
}
