import {
    SET_SELECTED_PRODUCT,
    ADD_TO_CART,
    REMOVE_FROM_CART,
    INCREASE_IN_CART,
    DECREASE_IN_CART,
    SET_ACTIVATED_COUPON,
    SET_CART_EMPTY,
} from '../types';

export const setSelectedProduct = (payload: any) => {
    return (dispatch) => {
        dispatch({ type: SET_SELECTED_PRODUCT, payload });
    }
}

export const addToCart = (payload: any) => {
    return (dispatch) => {
        dispatch({ type: ADD_TO_CART, payload });
    }
}

export const setCartEmpty = () => {
    const cart = [];
    return (dispatch) => {
        dispatch({ type: SET_CART_EMPTY, payload: cart });
    }
}

export const removeFromCart = (payload: any) => {
    return (dispatch) => {
        dispatch({ type: REMOVE_FROM_CART, payload });
    }
}

export const increaseInCart = (payload: any) => {
    return (dispatch) => {
        dispatch({ type: INCREASE_IN_CART, payload });
    }
}

export const decreaseInCart = (payload: any) => {
    return (dispatch) => {
        dispatch({ type: DECREASE_IN_CART, payload });
    }
}

export const setActivatedCoupon = (payload: any) => {
    return (dispatch) => {
        dispatch({ type: SET_ACTIVATED_COUPON, payload });
    }
}
