import Axios from 'axios-observable';
import { Observable } from 'rxjs';
import { environment } from '../environment';

export class ProductController {
    EndPoint = environment.API_ENDPOINT + `products/`;

    constructor() { }

    filter(
        option: {
            take?: any;
            page?: any;
            searchTerm?: any;
            brand?: any;
            isAvailable?: any;
            isPopular?: any;
            isFeatured?: any;
            isNewArrival?: any;
            isTopSelling?: any;
            isOnline?: any;
            hasDiscount?: any;
            hasPromotion?: any;
            department?: any;
            category?: any;
            subCategory?: any;
            price?: any;
            rating?: any;
            sort?: any;
        } = {}
    ) {
        return Axios.get(
            `${this.EndPoint}filter?searchTerm=${option.searchTerm ||
            ''}&brand=${option.brand ||
            ''}&isAvailable=${option.isAvailable ? 'true' : '' ||
                ''}&isPopular=${option.isPopular ? 'true' : '' ||
                    ''}&isFeatured=${option.isFeatured ? 'true' : '' ||
                        ''}&isNewArrival=${option.isNewArrival ? 'true' : '' ||
                            ''}&isTopSelling=${option.isTopSelling ? 'true' : '' ||
                                ''}&hasDiscount=${option.hasDiscount ? 'true' : '' ||
                                    ''}&hasPromotion=${option.hasPromotion ? 'true' : '' ||
                                        ''}&department=${option.department ||
                                        ''}&category=${option.category ||
                                        ''}&subCategory=${option.subCategory ||
                                        ''}&price=${option.price ||
                                        ''}&rating=${option.rating ||
                                        ''}&sort=${option.sort ||
                                        ''}&take=${option.take ||
                                        ''}&page=${option.page || ''}&isOnline=ONLINE&isActive=1`
        );
    }

    getAllProducts(routeLink, take, page) {
        return Axios.get(`${this.EndPoint}allproducts?routeLink=${routeLink}&take=${take}&page=${page}`);
    }

    rateProduct(product: string, rate: any) {
        return Axios.get(`${this.EndPoint}rateProduct?product=${product}&rate=${rate}`);
    }

    getByUuid(uuid: string) {
        return Axios.get(`${this.EndPoint}${uuid}`);
    }

    create(payload: any): Observable<any> {
        return Axios.post(this.EndPoint, { ...payload });
    }

    delete(uuid: string): Observable<any> {
        return Axios.delete(this.EndPoint + uuid);
    }

    update(uuid: string, payload: any): Observable<any> {
        return Axios.put(this.EndPoint + uuid, { ...payload });
    }
}
