import {
    SET_USER, SET_AUTH,
    SET_ROUTE_LINK,
} from '../types';

const INITIAL_STATE = {
    user: null,
    routeLink: 'Home',
};

export default (state = INITIAL_STATE, action) => {

    switch (action.type) {

        case SET_ROUTE_LINK:
            return { ...state, routeLink: action.payload };

        case SET_USER:
            return { ...state, user: action.payload };

        case SET_AUTH:
            return { ...state, auth: action.payload };

        default:
            return state;

    }
};
