import React from 'react';
import { FlatList, Text, TouchableOpacity, View } from 'react-native';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';
import FAIcon from 'react-native-vector-icons/FontAwesome';
import { connect } from 'react-redux';
import { MowButtonBasic } from '../../components/ui/Common/Button/MowButton';
import MowContainer from '../../components/ui/Core/Container/MowContainer';
import { MowInfoHeader } from '../../components/ui/MowInfoHeader';
import { DeliveryTimingController } from '../../controller/deliveryTiming.controller';
import { mowColors } from '../../values/Colors/MowColors';
import { fontFamily } from '../../values/Styles/MowStyles';
import { setDeliveryTiming } from '../../store/actions/orderActions';

interface IProps {
    user: any;
    product: any;
    navigation: any;
    setDeliveryTiming(data): any;

}

interface IState {
    selected: any;
    datas: any[];
}

class DeliveryTimingScreen extends React.Component<IProps, IState> {

    _unsubscribe = null;
    modelController = new DeliveryTimingController();

    constructor(props) {
        super(props);

        this.state = {
            selected: null,
            datas: []
        };
    }

    componentDidMount() {
        this._unsubscribe = this.props.navigation.addListener('focus', () => {
            this.getData();
        });
    }

    getData = () => {
        this.modelController.getAll({ take: 'all' })
            .subscribe((result: any) => {
                const data = result.data;
                this.setState({ datas: data.data, selected: data.data[0] });
            })
    }

    onSelect = (item) => {
        this.setState({ selected: item });
    }

    isSelected = (item) => {
        const { selected } = this.state;
        if (selected.uuid === item.uuid) {
            return true;
        } else {
            return false;
        }
    }

    componentWillUnmount() {
        this._unsubscribe();
    }

    render() {
        return (
            <MowContainer
                navigation={this.props.navigation}
                style={{ backgroundColor: mowColors.pageBGDarkColor }}
                title={'Delivery Timing'}>
                {
                    this.props.product.cart.length > 0 ?
                        <MowInfoHeader
                            activeIndex={3} /> : null
                }

                {
                    this.state.datas.length > 0 ?
                        <FlatList
                            showsVerticalScrollIndicator={false}
                            keyExtractor={(item, index) => index.toString()}
                            data={this.state.datas}
                            style={{ marginTop: 10 }}
                            renderItem={({ item, index }) => (
                                <TouchableOpacity
                                    onPress={() => { this.setState({ selected: item }) }}
                                    style={{
                                        marginTop: 20,
                                        flexDirection: 'row',
                                        padding: 10,
                                        borderRadius: 5,
                                        marginHorizontal: wp('4%'),
                                        backgroundColor: 'white',
                                        borderStyle: 'solid',
                                        borderWidth: 1,
                                        borderColor: 'rgba(146, 146, 146, 0.41)',
                                        marginVertical: 5,
                                    }}>
                                    <View
                                        style={{ width: '90%' }}>
                                        <Text
                                            style={{
                                                fontSize: hp('2%'),
                                                fontStyle: 'normal',
                                                color: mowColors.textColor,
                                                fontWeight: '600',
                                                paddingLeft: 10,
                                                fontFamily: fontFamily.regular,
                                            }}>
                                            {item.name}
                                        </Text>
                                    </View>
                                    <View
                                        style={{ width: '10%' }}>
                                        <FAIcon
                                            style={{
                                                fontSize: hp('2.5%'),
                                                color: this.isSelected(item) ? mowColors.mainColor : '#b4b4b4',
                                            }}
                                            name={this.isSelected(item) ? 'circle' : 'circle-thin'} />

                                    </View>

                                </TouchableOpacity>

                            )}
                        /> : null
                }

                {
                    this.state.selected !== null ?
                        <View
                            style={{ margin: 10 }}>
                            <MowButtonBasic
                                onPress={() => {
                                    this.props.setDeliveryTiming(this.state.selected);
                                    this.props.navigation.navigate('PaymentMethod');
                                }}
                                type={'success'}>
                                Continue
                            </MowButtonBasic>
                        </View> : null
                }

            </MowContainer>
        );
    }
}

const mapStateToProps = (state) => {
    const { user, product } = state;
    return { user, product };
};
const mapDispatchToProps = {
    setDeliveryTiming: setDeliveryTiming,
};
export default connect(mapStateToProps, mapDispatchToProps)(DeliveryTimingScreen);
