import React from 'react';
import { FlatList, Image, ScrollView, Text, View } from 'react-native';
import { heightPercentageToDP as hp } from 'react-native-responsive-screen';
import { connect } from 'react-redux';
import MowContainer from '../../components/ui/Core/Container/MowContainer';
import { MowTitleView } from '../../components/ui/MowTitleView';
import { AdvertisementController } from '../../controller/advertisement.controller';
import { CategoryController } from '../../controller/category.controller';
import { DepartmentController } from '../../controller/department.controller';
import Advantages from '../../sampleData/Advantages';
import { mowColors } from '../../values/Colors/MowColors';
import { categoryStyle } from '../../values/Styles/MowStyles';
import { Banner } from './components/banner.component';
import { FeaturedCategories } from './components/featuredCategories.component';
import { FeaturedDepartments } from './components/featuredDepartments.component';
import { FullAdsOne } from './components/fullAdsOne.component';
import { FullAdsTwo } from './components/fullAdsTwo.component';
import { HalfAds } from './components/halfAds.component';
import { Logo } from './components/logo.component';
import { Search } from './components/search.component';
import { TopCollection } from './components/topCollection.component';
import { Loading } from '../../components/Loading/loading';
import { setDepartment, setCategory } from '../../store/actions/categoryActions';

interface IProps {
    navigation: any;
    user: any;
    setDepartment(data): any;
    setCategory(data): any;
}

interface IState {
    featuredDepartments: any;
    featuredCategories: any;
    banners: any;
    topCollections: any;
    halfAds: any;
    fullAdsOne: any;
    fullAdsTwo: any;
}

class HomeScreen extends React.Component<IProps, IState> {

    departmentController = new DepartmentController();
    categoryController = new CategoryController();
    advertisementController = new AdvertisementController();

    constructor(props) {
        super(props);
        this.state = {
            featuredDepartments: null,
            featuredCategories: null,
            banners: null,
            topCollections: null,
            halfAds: null,
            fullAdsOne: null,
            fullAdsTwo: null,
        }
    }

    componentDidMount() {
        this.getDepartments();
        this.getCategories();
        this.getBanners();
        this.getTopCollections();
        this.gethalfAdvertisements();
        this.getFullAdvertisementsOne();
        this.getFullAdvertisementsTwo();
    }

    getDepartments = () => {
        this.departmentController.getAll({ take: 'all', isFeatured: 1, isActive: 1 })
            .subscribe((result: any) => {
                const data = result.data;
                this.setState({ featuredDepartments: data.data });
            });
    }

    getCategories = () => {
        this.categoryController.getAll({ take: 'all', isFeatured: 1, isActive: 1 })
            .subscribe((result: any) => {
                const data = result.data;
                this.setState({ featuredCategories: data.data });
            });
    }

    getBanners = () => {
        this.advertisementController.getAll({ take: 'all', active: 1, type: 'banner' })
            .subscribe((result: any) => {
                const data = result.data;
                this.setState({ banners: data.data });
            });
    }

    getTopCollections = () => {
        this.advertisementController.getAll({ take: 'all', active: 1, type: 'topCollection' })
            .subscribe((result: any) => {
                const data = result.data;
                this.setState({ topCollections: data.data });
            });
    }

    gethalfAdvertisements = () => {
        this.advertisementController.getAll({ take: 'all', active: 1, type: 'halfAdvertisement' })
            .subscribe((result: any) => {
                const data = result.data;
                this.setState({ halfAds: data.data });
            });
    }

    getFullAdvertisementsOne = () => {
        this.advertisementController.getAll({ take: 'all', active: 1, type: 'fullAdvertisementOne' })
            .subscribe((result: any) => {
                const data = result.data;
                this.setState({ fullAdsOne: data.data });
            });
    }

    getFullAdvertisementsTwo = () => {
        this.advertisementController.getAll({ take: 'all', active: 1, type: 'fullAdvertisementTwo' })
            .subscribe((result: any) => {
                const data = result.data;
                this.setState({ fullAdsTwo: data.data });
            });
    }

    onClickFeaturedDepartment = (item) => {
        this.props.setDepartment(item);
        this.props.navigation.navigate('Categories');
    }

    onClickFeaturedCategory = (item) => {
        this.props.setDepartment(item.department);
        this.props.setCategory(item);
        this.props.navigation.navigate('SubCategories');
    }

    render() {
        const {
            featuredDepartments,
            banners,
            topCollections,
            halfAds,
            fullAdsOne,
            featuredCategories,
            fullAdsTwo,
        } = this.state;

        return (

            <MowContainer
                navigation={this.props.navigation}
                footerActiveIndex={1}
                navbar={false}>

                <Logo />
                <Search navigation={this.props.navigation} />

                <ScrollView>
                    {
                        featuredDepartments === null ?
                            <Loading /> :
                            <FeaturedDepartments
                                onClick={this.onClickFeaturedDepartment}
                                navigation={this.props.navigation} data={featuredDepartments} />
                    }

                    {
                        banners === null ?
                            <Loading /> :
                            <Banner navigation={this.props.navigation} data={banners} />
                    }

                    {
                        topCollections === null ?
                            <Loading /> :
                            <TopCollection navigation={this.props.navigation} data={topCollections} />
                    }

                    {
                        halfAds === null ?
                            <Loading /> :
                            <HalfAds navigation={this.props.navigation} data={halfAds} />
                    }

                    {
                        fullAdsOne === null ?
                            <Loading /> :
                            <FullAdsOne navigation={this.props.navigation} data={fullAdsOne} />
                    }

                    {
                        featuredCategories === null ?
                            <Loading /> :
                            <FeaturedCategories
                                onClick={this.onClickFeaturedCategory}
                                navigation={this.props.navigation} data={featuredCategories} />
                    }

                    {
                        fullAdsTwo === null ?
                            <Loading /> :
                            <FullAdsTwo navigation={this.props.navigation} data={fullAdsTwo} />
                    }

                    <View
                        style={[categoryStyle, { marginTop: 15, backgroundColor: mowColors.categoryBGColor }]}>
                        <MowTitleView
                            showButton={false}
                            title='Features' />
                        <FlatList
                            horizontal={true}
                            showsHorizontalScrollIndicator={false}
                            keyExtractor={(item, index) => index.toString()}
                            data={Advantages}
                            renderItem={({ item, index }) => (
                                <View
                                    style={{
                                        width: hp('12%'),
                                        height: hp('11%'),
                                        marginRight: 10,
                                        justifyContent: 'center',
                                        alignItems: 'center',
                                    }}>
                                    <View
                                        style={{
                                            borderRadius: 10,
                                            backgroundColor: mowColors.mainColor,
                                            width: '100%',
                                            height: hp('8%'),
                                            justifyContent: 'center',
                                            alignItems: 'center',
                                        }}
                                        key={index}>
                                        <Image
                                            style={{ width: hp('6%'), height: hp('6%') }}
                                            source={item.image}
                                            resizeMode={'contain'} />

                                    </View>
                                    <Text
                                        style={{
                                            marginTop: 5,
                                            fontSize: hp('1.4%'),
                                            fontWeight: 'normal',
                                            fontStyle: 'normal',
                                            letterSpacing: 0,
                                            textAlign: 'center',
                                            color: mowColors.textColor,
                                        }}>
                                        {item.title}
                                    </Text>
                                </View>
                            )}
                        />
                    </View>
                </ScrollView>
            </MowContainer>
        );
    }
}

const mapStateToProps = (state) => {
    const { user } = state;
    return { user };
};
const mapDispatchToProps = {
    setDepartment: setDepartment,
    setCategory: setCategory,
};
export default connect(mapStateToProps, mapDispatchToProps)(HomeScreen);
