    INPUT_TYPE_KINECT,
    INPUT_TYPE_MOUSE,
    INPUT_TYPE_PEN,
    INPUT_TYPE_TOUCH,
    COMPUTE_INTERVAL,
    INPUT_START,
    INPUT_MOVE,
    INPUT_END,
    INPUT_CANCEL,
    DIRECTION_NONE,
    DIRECTION_LEFT,
