  let isFirst = (eventType & INPUT_START && (pointersLen - changedPointersLen === 0));
  let isFinal = (eventType & (INPUT_END | INPUT_CANCEL) && (pointersLen - changedPointersLen === 0));

  input.isFirst = !!isFirst;
