import React from 'react';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import { footerHeight } from "../../../../values/Constants/MowConstants";
import FatherIcon from "react-native-vector-icons/Feather";
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';
import { mowColors } from "../../../../values/Colors/MowColors";
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { setRouteLink } from '../../../../store/actions/userActions';

class MowFooter extends React.Component {

    // footer props
    static propTypes = {
        activeIndex: PropTypes.number,
        navigation: PropTypes.any,
        user: PropTypes.any,
    };

    componentDidMount() {
    }

    handleNavigation(route) {
        if (this.props.user.user === null) {
            this.props.setRouteLink(route);
            this.props.navigation.navigate('Login');
        } else {
            this.props.navigation.navigate(route);
        }
    }

    render() {

        return (

            <View
                style={{
                    height: footerHeight,
                    position: 'absolute',
                    bottom: 0,
                    left: 0,
                    right: 0,
                    width: '100%',
                    flexDirection: "row",
                    backgroundColor: mowColors.footer,
                    shadowColor: "rgba(0, 0, 0, 0.11)",
                    shadowOffset: {
                        width: 0,
                        height: -3
                    },
                    shadowRadius: 4,
                    shadowOpacity: 1,
                    borderTopWidth: 0.5,
                    borderTopColor: "#a1a1a1"
                }}>
                <TouchableOpacity
                    onPress={() => { this.props.navigation.navigate("Home") }}
                    style={styles.buttonView}>
                    <FatherIcon
                        name={"home"}
                        style={[styles.buttonIcon, { color: this.props.activeIndex === 1 ? mowColors.mainColor : "#a1a1a1" }]} />
                    <Text
                        style={[styles.buttonText, { color: this.props.activeIndex === 1 ? mowColors.mainColor : "#a1a1a1" }]}>
                        Home
                    </Text>
                </TouchableOpacity>
                <TouchableOpacity
                    onPress={() => { this.props.navigation.navigate("Departments") }}
                    style={styles.buttonView}>
                    <FatherIcon
                        name={"grid"}
                        style={[styles.buttonIcon, { color: this.props.activeIndex === 2 ? mowColors.mainColor : "#a1a1a1" }]} />
                    <Text
                        style={[styles.buttonText, { color: this.props.activeIndex === 2 ? mowColors.mainColor : "#a1a1a1" }]}>
                        Categories
                    </Text>
                </TouchableOpacity>
                <TouchableOpacity
                    onPress={() => { this.handleNavigation('Cart') }}
                    style={styles.buttonView}>
                    <FatherIcon
                        name={"shopping-bag"}
                        style={[styles.buttonIcon, { color: this.props.activeIndex === 3 ? mowColors.mainColor : "#a1a1a1" }]} />
                    <Text
                        style={[styles.buttonText, { color: this.props.activeIndex === 3 ? mowColors.mainColor : "#a1a1a1" }]}>
                        Cart
                    </Text>
                </TouchableOpacity>
                <TouchableOpacity
                    onPress={() => { this.handleNavigation('Orders') }}
                    style={styles.buttonView}>
                    <FatherIcon
                        name={"box"}
                        style={[styles.buttonIcon, { color: this.props.activeIndex === 4 ? mowColors.mainColor : "#a1a1a1" }]} />
                    <Text
                        style={[styles.buttonText, { color: this.props.activeIndex === 4 ? mowColors.mainColor : "#a1a1a1" }]}>
                        Orders
                    </Text>
                </TouchableOpacity>
            </View>
        );
    }
}

const mapStateToProps = (state) => {
    const { user } = state;
    return { user };
};
const mapDispatchToProps = {
    setRouteLink: setRouteLink,
};
export default connect(mapStateToProps, mapDispatchToProps)(MowFooter);

const styles = StyleSheet.create({
    buttonView: {
        flex: 1,
        alignItems: "center",
        height: "100%"
    },
    buttonIcon: {
        marginTop: hp("1%"),
        fontSize: wp("5.2%"),
    },
    buttonText: {
        fontSize: wp("3.5%"),
        marginTop: hp("0.5%")
    }
});
