import React from "react";
import PropTypes from 'prop-types';
import { View, Text } from "react-native";
import FAIcon from "react-native-vector-icons/FontAwesome";
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from "react-native-responsive-screen";
import { mowColors } from "../../values/Colors/MowColors";

export class MowInfoHeader extends React.Component {

    static propTypes = {
        activeIndex: PropTypes.oneOf([1, 2, 3, 4])
    };

    static defaultProps = {
        activeIndex: 0
    };

    render() {
        return (
            <View
                style={{
                    flexDirection: "row",
                    alignItems: "center",
                    justifyContent: "center",
                    paddingVertical: hp("1%"),
                    backgroundColor: mowColors.viewBGColor
                }}>
                <View
                    style={textView.view}>
                    <Text
                        style={[textView.text, { color: this.props.activeIndex === 1 ? mowColors.mainColor : mowColors.textColor }]}>
                        Billing Address
                    </Text>
                </View>
                <FAIcon
                    style={[iconView.icon, { color: this.props.activeIndex === 1 ? mowColors.mainColor : mowColors.textColor }]}
                    name={"chevron-right"} />
                <View
                    style={textView.view}>
                    <Text
                        style={[textView.text, { color: this.props.activeIndex === 2 ? mowColors.mainColor : mowColors.textColor }]}>
                        Shipping Address
                    </Text>
                </View>
                <FAIcon
                    style={[iconView.icon, { color: this.props.activeIndex === 2 ? mowColors.mainColor : mowColors.textColor }]}
                    name={"chevron-right"} />
                <View
                    style={textView.view}>
                    <Text
                        style={[textView.text, { color: this.props.activeIndex === 3 ? mowColors.mainColor : mowColors.textColor }]}>
                        Delivery Timing
                    </Text>
                </View>
                <FAIcon
                    style={[iconView.icon, { color: this.props.activeIndex === 3 ? mowColors.mainColor : mowColors.textColor }]}
                    name={"chevron-right"} />
                <View
                    style={textView.view}>
                    <Text
                        style={[textView.text, { color: this.props.activeIndex === 4 ? mowColors.mainColor : mowColors.textColor }]}>
                        Payment
                    </Text>
                </View>
            </View>
        )
    }
}

const textView = ({
    view: {
        flex: 1
    },
    text: {
        fontSize: hp("1.8%"),
        fontWeight: "500",
        fontStyle: "normal",
        lineHeight: 18,
        letterSpacing: 0,
        textAlign: "left",
        marginLeft: wp("5%")
    }
});

const iconView = ({
    icon: {
        fontSize: hp("2.5%")
    }
});
