import React from 'react';
import { AsyncStorage, Text, View } from 'react-native';
import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scroll-view';
import { heightPercentageToDP as hp } from 'react-native-responsive-screen';
import { connect } from 'react-redux';
import { MowButtonBasic } from '../../../components/ui/Common/Button/MowButton';
import { MowInput } from '../../../components/ui/Common/Input/MowInput';
import MowContainer from '../../../components/ui/Core/Container/MowContainer';
import { setAuth, setUser } from '../../../store/actions/userActions';
import { USER, LoginPhoneNumber } from '../../../store/types';
import { mowColors } from '../../../values/Colors/MowColors';
import { pageContainerStyle } from '../../../values/Styles/MowStyles';
import { AuthController } from '../../../controller/auth.controller';

const iconColor = 'white';

interface IProps {
  navigation: any;
  setUser(user: any): any;
  setAuth(auth: boolean): any;
}

interface IState {
  isLoading: boolean;
  phoneNumber: any;
  error: string;
  isLoginDisabled: boolean;
}

class LoginScreen extends React.Component<IProps, IState> {

  authController = new AuthController();

  constructor(props) {
    super(props);

    this.state = {
      isLoading: false,
      phoneNumber: null,
      error: '',
      isLoginDisabled: true,
    }
  }

  validatePhoneNumber = (phoneNumber) => {
    const regex = /\+?(88)?0?1[3456789][0-9]{8}\b/;
    return regex.test(phoneNumber);
  };

  // validateOTP = (otp) => {
  //   const regex = /^[0-9]{7}$/;
  //   return regex.test(otp);
  // };

  onChangePhoneNumber(value) {
    if (this.validatePhoneNumber(value)) {
      this.setState({ error: '', isLoginDisabled: false, phoneNumber: value });
    } else {
      this.setState({ error: 'Invalid Phone Number', isLoginDisabled: true });
    }
  }

  login = async () => {
    const { phoneNumber } = this.state;
    this.setState({ isLoginDisabled: true, isLoading: true });

    this.authController.login({ phoneNumber })
      .subscribe(async (result: any) => {
        const data = result.data;
        if (data.success) {
          await AsyncStorage.setItem(LoginPhoneNumber, phoneNumber);
          this.setState({ isLoading: false });
          this.props.navigation.navigate('Verify');
        } else {
          this.setState({ error: 'Something Wrong', isLoading: false });
        }
      })
  }

  renderLoginView = (): React.ReactElement => (
    <View
      style={{ ...pageContainerStyle, marginTop: hp('3%') }}>

      <Text
        style={{
          fontSize: hp(3),
          fontWeight: '600',
          fontStyle: 'normal',
          textAlign: 'center',
          color: '#ffffff',
          marginBottom: hp(5)
        }}>
        One Click Login With Phone Number
      </Text>

      <View style={inputStyle.container}>
        <Text>Phone Number</Text>
        <MowInput
          type='number'
          iconColor={iconColor}
          containerStyle={inputStyle.inputContainer}
          textInputStyle={inputStyle.inputText}
          onChangeText={value => { this.onChangePhoneNumber(value) }} />
        <Text
          style={{
            fontSize: hp(2),
            fontWeight: '600',
            fontStyle: 'normal',
            textAlign: 'center',
            color: 'red',
            marginBottom: hp(5)
          }}>
          {this.state.error}
        </Text>
      </View>

      <MowButtonBasic
        disabled={this.state.isLoginDisabled}
        onPress={this.login}
        style={{ marginTop: hp('3%') }}
        containerStyle={{ marginTop: hp('5%') }}
        textStyle={{ color: mowColors.mainColor, fontWeight: 'normal', letterSpacing: 0 }}
        type={'default'}>

        {this.state.isLoading ? 'Please Wait' : 'Login'}

      </MowButtonBasic>

    </View>
  )

  render() {
    return (
      <MowContainer
        navigation={this.props.navigation}
        footer={false}
        hideStatusBar={true}
        navbar={false}
        style={{ backgroundColor: mowColors.mainColor }}>

        <KeyboardAwareScrollView
          showsVerticalScrollIndicator={false}
          style={pageContainerStyle}>

          {
            this.renderLoginView()
          }

        </KeyboardAwareScrollView>

      </MowContainer>
    )
  }

}

export const inputStyle = ({
  container: {
    marginVertical: 10
  },
  titleText: {
    fontSize: hp('2%'),
    fontWeight: 'normal',
    fontStyle: 'normal',
    letterSpacing: 0,
    textAlign: 'left',
    color: '#ffffff',
    opacity: 0.8
  },
  inputContainer: {
    backgroundColor: 'transparent',
    orderStyle: 'solid',
    borderWidth: 0,
    borderBottomWidth: 1,
    borderBottomColor: '#ffffff',
    width: '100%'
  },
  inputText: {
    fontSize: hp('2.2%'),
    fontWeight: '500',
    fontStyle: 'normal',
    letterSpacing: 0,
    textAlign: 'left',
    color: '#ffffff',
    width: '85%'
  },
});

const mapStateToProps = (state) => {
  const { user } = state;
  return { user };
};
const mapDispatchToProps = {
  setUser: setUser,
  setAuth: setAuth,
};
export default connect(mapStateToProps, mapDispatchToProps)(LoginScreen);
