import {
    SET_SELECTED_PRODUCT,
    ADD_TO_CART,
    REMOVE_FROM_CART,
    INCREASE_IN_CART,
    DECREASE_IN_CART,
    SET_ACTIVATED_COUPON,
    SET_CART_EMPTY,
} from '../types';

const INITIAL_STATE = {
    selectedProduct: null,
    cart: [],
    activatedCoupon: null,
};

export default (state = INITIAL_STATE, action) => {

    switch (action.type) {

        case SET_SELECTED_PRODUCT:
            return { ...state, selectedProduct: action.payload };

        case SET_CART_EMPTY:
            return { ...state, cart: action.payload };

        case SET_ACTIVATED_COUPON:
            return { ...state, activatedCoupon: action.payload };

        case ADD_TO_CART:
            const item = {
                quantity: 1,
                uuid: action.payload.uuid,
                product: action.payload,
            }

            const tempCart = state.cart;
            tempCart.push(item)
            return { ...state, cart: tempCart };

        case REMOVE_FROM_CART:
            const _cart = state.cart.filter(o => o.uuid !== action.payload.uuid);
            return { ...state, cart: _cart };

        case INCREASE_IN_CART:
            state.cart.map(o => {
                if (o.uuid === action.payload.uuid) {
                    o.quantity++;
                }
            })
            return { ...state, cart: state.cart };

        case DECREASE_IN_CART:
            state.cart.map(o => {
                if (o.uuid === action.payload.uuid) {
                    if (o.quantity > 1) {
                        o.quantity--;
                    }
                }
            })
            return { ...state, cart: state.cart };

        default:
            return state;

    }
};
