import React from 'react';
import { FlatList, Image, View, TouchableOpacity } from 'react-native';
import { widthPercentageToDP as wp, heightPercentageToDP as hp } from 'react-native-responsive-screen';
import { mowColors } from '../../../values/Colors/MowColors';
import { categoryStyle } from '../../../values/Styles/MowStyles';

interface IProps {
    navigation: any;
    data: any;
}

export const FullAdsOne = ({ navigation, data }): React.ReactElement => {

    return (
        <View
            style={[categoryStyle, { marginTop: 15, backgroundColor: mowColors.categoryBGColor }]}>
            <FlatList
                horizontal={true}
                showsHorizontalScrollIndicator={false}
                keyExtractor={(item, index) => index.toString()}
                data={data}
                renderItem={({ item, index }) => (

                    <TouchableOpacity
                        style={{
                            width: wp('90%'),
                            height: hp('15%'),
                            justifyContent: 'center',
                            backgroundColor: 'transparent',
                            borderStyle: 'solid',
                            borderWidth: 1,
                            borderColor: 'rgba(146, 146, 146, 0.41)',
                            borderRadius: 5,
                            marginHorizontal: 10,
                            marginVertical: 5,
                            alignItems: 'center',
                        }}
                        key={index}>

                        <Image
                            style={{ width: wp('90%'), height: hp('15%') }}
                            source={{ uri: item.image }}
                            resizeMode={'stretch'} />

                    </TouchableOpacity>

                )}
            />

        </View>
    )
}
