import React, { useRef } from 'react';
import { Image, View } from 'react-native';
import { widthPercentageToDP as wp } from 'react-native-responsive-screen';
import Swiper from 'react-native-swiper';
import { mowColors } from '../../../values/Colors/MowColors';
import { categoryStyle } from '../../../values/Styles/MowStyles';

interface IProps {
    navigation: any;
    data: any;
}

export const Banner = ({ navigation, data }): React.ReactElement => {

    const swiper = useRef(null);

    return (
        <View
            style={[categoryStyle, {
                marginTop: 15,
                height: wp('75%'),
                backgroundColor: mowColors.categoryBGColor,
            }]}>

            <Swiper
                ref={swiper}
                pagingEnabled={true}
                showsPagination={true}
                horizontal={true}
                loop={false}
                dotColor={mowColors.titleTextColor}
                activeDotColor={mowColors.mainColor}
                paginationStyle={{ bottom: 0 }}
                autoplay={false}>

                {
                    data.map((item, key) => {
                        return (

                            <View
                                key={key}
                                style={{
                                    flexDirection: 'row',
                                    width: '95%',
                                    alignSelf: 'center',
                                    height: wp('53.5%'),
                                    borderRadius: 10,
                                    marginLeft: '-5%',
                                }}>

                                <Image
                                    style={{
                                        position: 'absolute',
                                        alignSelf: 'center',
                                        zIndex: -1,
                                        borderRadius: 10,
                                        width: '100%',
                                        height: '100%',
                                    }}
                                    resizeMode={'stretch'}
                                    source={{ uri: item.image }}
                                />
                            </View>
                        );
                    })
                }
            </Swiper>
        </View>
    )
}
