import { addToCart, removeFromCart } from '../../store/actions/productActions';
import { _showToast } from '../../components/ui/Common/Toast/MowToast';

interface IProps {
    navigation: any;
    product: any;
    user: any;
    addToCart(data): any;
    removeFromCart(data): any;
}

interface IState {
    product: any;
}

class ProductDetailsScreen extends React.Component<IProps, IState> {

    _unsubscribe = null;

    state = {
        product: null,
    };

    componentDidMount() {
        this._unsubscribe = this.props.navigation.addListener('focus', () => {
            this.setState({ product: this.props.product.selectedProduct });
        })
    }

    componentWillUnmount() {
        this._unsubscribe();
    }

    getPromotion(product) {
        const user = this.props.user.user;
        if (user !== null && product.hasPromotion) {
            if (user.isMembershipActivated === 1) {
                return `BDT ${product.promotionPriceOne} OFF`;
            } else {
                return `BDT ${product.promotionMrp} OFF`;
            }
        } else {
            return false;
        }
    }

    getPromotionalPrice(product) {
        const user = this.props.user.user;
        if (user !== null && user.isMembershipActivated === 1) {
            if (product.hasPromotion) {
                return product.priceOne - product.promotionPriceOne;
            } else {
                return product.priceOne;
            }
        } else {
            if (product.hasPromotion) {
                return product.mrp - product.promotionMrp;
            } else {
                return product.mrp;
            }
        }
    }

    getActualPrice(product) {
        const user = this.props.user.user;
        if (user !== null && user.isMembershipActivated === 1) {
            return product.priceOne;
        } else {
            return product.mrp;
        }
    }

    _addToCart = () => {
        this.props.addToCart(this.state.product);
        _showToast.success('Added');
    }

    _removeFromCart = () => {
        this.props.removeFromCart(this.state.product);
        _showToast.warning('Removed');
    }

    isProductExistInCart() {
        const cart = this.props.product.cart;
        const isExist = cart.filter(o => o.uuid === this.state.product.uuid);

        if (isExist.length > 0) {
            return true;
        } else {
            return false;
        }
    }

