import React from 'react';
import { FlatList, Image, Text, TouchableOpacity, View } from 'react-native';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';
import { MowTitleView } from '../../../components/ui/MowTitleView';
import { mowColors } from '../../../values/Colors/MowColors';
import { categoryStyle } from '../../../values/Styles/MowStyles';

export const FeaturedCategories = ({ navigation, data, onClick }): React.ReactElement => {

    return (
        <View
            style={[categoryStyle, { marginTop: 15, backgroundColor: mowColors.categoryBGColor }]}>
            <MowTitleView
                showButton={false}
                title='Featured Categories' />
            <FlatList
                horizontal={true}
                showsHorizontalScrollIndicator={false}
                keyExtractor={(item, index) => index.toString()}
                data={data}
                renderItem={({ item, index }) => (
                    <TouchableOpacity onPress={() => onClick(item)}>
                        <View
                            style={{
                                width: wp('35%'),
                                height: hp('25%'),
                                marginHorizontal: 10,
                            }}
                            key={index}>
                            <View
                                style={{
                                    height: '53%',
                                    width: '100%',
                                    borderRadius: 10,
                                    borderStyle: 'solid',
                                    borderWidth: 1,
                                    borderColor: 'rgba(112, 112, 112, 0.16)',
                                    justifyContent: 'center',
                                }}>

                                <Image
                                    style={{
                                        height: '100%',
                                        width: '100%',
                                        borderRadius: 10,
                                    }}
                                    resizeMode={'contain'}
                                    source={{ uri: item.image }} />

                            </View>

                            <Text
                                style={{
                                    fontSize: hp('2%'),
                                    fontWeight: 'bold',
                                    fontStyle: 'normal',
                                    letterSpacing: 0,
                                    textAlign: 'left',
                                    color: mowColors.titleTextColor,
                                    marginTop: 5,
                                }}>

                                {item.name}

                            </Text>

                        </View>
                    </TouchableOpacity>

                )}
            />

        </View>
    )
};
