import Axios from 'axios-observable';
import { Observable } from 'rxjs';
import { environment } from '../environment';

export class CategoryController {
    EndPoint = environment.API_ENDPOINT + `categories/`;

    constructor() { }

    getAll(
        option: {
            take?: any;
            page?: any;
            department?: any;
            isFeatured?: any;
            isActive?: any;
        } = {}
    ) {
        return Axios.get(
            `${this.EndPoint}?take=${option.take || ''}&page=${option.page || ''}&isFeatured=${option.isFeatured || ''}&isActive=${option.isActive || ''}&department=${option.department || ''}`
        );
    }

    getByUuid(uuid: string) {
        return Axios.get(`${this.EndPoint}${uuid}`);
    }

    create(payload: any): Observable<any> {
        return Axios.post(this.EndPoint, { ...payload });
    }

    delete(uuid: string): Observable<any> {
        return Axios.delete(this.EndPoint + uuid);
    }

    update(uuid: string, payload: any): Observable<any> {
        return Axios.put(this.EndPoint + uuid, { ...payload });
    }
}
