import React from "react";
import { AsyncStorage, Image, ScrollView, StyleSheet, Text, TouchableOpacity, View } from "react-native";
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';
import FeatherIcon from "react-native-vector-icons/Feather";
import FAIcon from "react-native-vector-icons/FontAwesome";
import { connect } from 'react-redux';
import { setAuth, setRouteLink, setUser } from '../../../../store/actions/userActions';
import { USER } from "../../../../store/types";
import { mowColors } from "../../../../values/Colors/MowColors";
import { fontFamily } from "../../../../values/Styles/MowStyles";
import MowStatusBar from "../StatusBar/MowStatusBar";

class MowSidebar extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            user: null
        }
    }

    async _handleLogout() {
        await AsyncStorage.removeItem(USER);
        this.props.setAuth(false);
        this.props.setUser(null);
        this.props.setRouteLink('Home');
        this.props.navigation.navigate('Home');
    }

    async componentDidMount() {
        const user = await AsyncStorage.getItem(USER);
        this.setState({ user });
    }

    render() {

        let nav = this.props.navigation;

        return (

            <View
                style={{ flex: 1, backgroundColor: mowColors.mainColor }}>

                <MowStatusBar />

                <View
                    style={{
                        height: hp("11%"),
                        paddingTop: 20,
                        width: '100%',
                        alignItems: 'center',
                        justifyContent: 'center',
                        alignSelf: 'center',
                        flexDirection: 'row',
                        paddingHorizontal: wp("5%")
                    }}>

                    <Image
                        source={require("../../../../assets/logo/app_logo.png")}
                        resizeMode={"contain"}
                        style={{
                            marginBottom: hp("3%"),
                            alignSelf: "center",
                            width: wp("40%"),
                            height: hp("5%")
                        }} />
                    {/* <TouchableOpacity
                        onPress={() => nav.navigate('Profile')}
                        style={{
                            flex: 1,
                            paddingLeft: 10,
                        }}>

                        <Image
                            resizeMode={'contain'}
                            source={require('../../../../assets/image/guest.png')}
                            style={{
                                width: hp("8%"),
                                height: hp("8%"),
                            }} />

                    </TouchableOpacity> */}
                    {/* 
                    <View
                        style={{ flex: 3 }}>

                        <Text
                            style={{
                                color: 'white',
                                fontWeight: '600',
                                fontSize: hp("2%"),
                                fontFamily: fontFamily.medium
                            }}>

                            Bianca Watkins

                        </Text>

                        <Text
                            style={{
                                color: 'white',
                                fontSize: hp("1.8%"),
                                padding: 3,
                                fontFamily: fontFamily.medium
                            }}>

                            biancawatkins@gmail.com

                        </Text>

                    </View> */}

                    <TouchableOpacity
                        onPress={() => { this.props.navigation.closeDrawer() }}
                        style={{ position: "absolute", top: 20, right: 15 }}>

                        <FAIcon
                            style={{ color: "white", fontSize: hp("3%") }}
                            name={"times"} />

                    </TouchableOpacity>

                </View>

                <ScrollView
                    style={{ marginTop: hp("3%"), marginBottom: hp("1%") }}>
                    {
                        this.props.user.user === null ? null :
                            <TouchableOpacity
                                style={styles.drawerItemView}
                                onPress={() => this.props.navigation.navigate('Profile')}>
                                <FeatherIcon
                                    style={styles.drawerIcon}
                                    name={"user"} />
                                <Text
                                    style={styles.drawerText}>
                                    Profile
                        </Text>
                            </TouchableOpacity>
                    }

                    {
                        this.props.user.user === null ? null :
                            <TouchableOpacity
                                style={styles.drawerItemView}
                                onPress={() => this.props.navigation.navigate('Membership')}>
                                <FeatherIcon
                                    style={styles.drawerIcon}
                                    name={"user"} />
                                <Text
                                    style={styles.drawerText}>
                                    Activate Membership
                        </Text>
                            </TouchableOpacity>
                    }

                    {/* cart button */}
                    {
                        this.props.user.user === null ? null :
                            <TouchableOpacity
                                style={styles.drawerItemView}
                                onPress={() => this.props.navigation.navigate('Cart')}>

                                <FeatherIcon
                                    style={styles.drawerIcon}
                                    name={"shopping-bag"} />

                                <Text
                                    style={styles.drawerText}>
                                    Cart
                        </Text>
                            </TouchableOpacity>
                    }

                    {
                        this.props.user.user === null ? null :
                            <TouchableOpacity
                                style={styles.drawerItemView}
                                onPress={() => this.props.navigation.navigate('Orders')}>

                                <FeatherIcon
                                    style={styles.drawerIcon}
                                    name={"shopping-bag"} />

                                <Text
                                    style={styles.drawerText}>
                                    Orders
                        </Text>
                            </TouchableOpacity>
                    }

                    <TouchableOpacity
                        style={styles.drawerItemView}
                        onPress={() => { }}>
                        <FeatherIcon
                            style={styles.drawerIcon}
                            name={"message-circle"} />
                        <Text
                            style={styles.drawerText}>
                            Feedback
                        </Text>
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={styles.drawerItemView}
                        onPress={() => { }}>
                        <FeatherIcon
                            style={styles.drawerIcon}
                            name={"message-square"} />
                        <Text
                            style={styles.drawerText}>
                            FAQ
                        </Text>
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={styles.drawerItemView}
                        onPress={() => { }}>
                        <FeatherIcon
                            style={styles.drawerIcon}
                            name={"phone-call"} />
                        <Text
                            style={styles.drawerText}>
                            Customer Service
                        </Text>
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={styles.drawerItemView}
                        onPress={() => { }}>
                        <FeatherIcon
                            style={styles.drawerIcon}
                            name={"shield"} />
                        <Text
                            style={styles.drawerText}>
                            Privacy
                        </Text>
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={styles.drawerItemView}
                        onPress={() => { }}>
                        <FeatherIcon
                            style={styles.drawerIcon}
                            name={"info"} />
                        <Text
                            style={styles.drawerText}>
                            About Us
                        </Text>
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={styles.drawerItemView}
                        onPress={() => { }}>
                        <FeatherIcon
                            style={styles.drawerIcon}
                            name={"command"} />
                        <Text
                            style={styles.drawerText}>
                            Contact Us
                        </Text>
                    </TouchableOpacity>
                    {
                        this.props.user.user !== null ?
                            <TouchableOpacity
                                style={styles.drawerItemView}
                                onPress={() => { this._handleLogout() }}>
                                <FeatherIcon
                                    style={styles.drawerIcon}
                                    name={"log-out"} />
                                <Text
                                    style={styles.drawerText}>
                                    Logout
                                </Text>
                            </TouchableOpacity> :
                            <TouchableOpacity
                                style={styles.drawerItemView}
                                onPress={() => { this.props.navigation.navigate('Login') }}>
                                <FeatherIcon
                                    style={styles.drawerIcon}
                                    name={"log-in"} />
                                <Text
                                    style={styles.drawerText}>
                                    Log In
                                </Text>
                            </TouchableOpacity>
                    }
                </ScrollView>
            </View>
        );
    }
}

const styles = StyleSheet.create({
    drawerIcon: {
        flex: 2,
        fontSize: hp("2.5%"),
        color: 'white',
        textAlign: "center"
    },
    drawerText: {
        flex: 8,
        fontSize: hp("2%"),
        color: 'white',
        marginLeft: 5,
        fontWeight: "500",
        fontStyle: "normal",
        letterSpacing: 0,
        fontFamily: fontFamily.medium
    },
    drawerItemView: {
        flexDirection: 'row',
        alignItems: 'center',
        margin: hp("1.5%"),
        marginVertical: hp("2%"),
        marginLeft: wp("5%"),
    }
});

const mapStateToProps = (state) => {
    const { user } = state;
    return { user };
};
const mapDispatchToProps = {
    setUser: setUser,
    setAuth: setAuth,
    setRouteLink: setRouteLink,
};
export default connect(mapStateToProps, mapDispatchToProps)(MowSidebar);
