import { SET_BILLING_ADDRESS, SET_DELIVERY_TIMING, SET_PAYMENT_METHOD, SET_SELECTED_ORDER, SET_SHIPPING_ADDRESS, SET_SHIPPING_AREA } from '../types';

const INITIAL_STATE = {
    billingAddress: null,
    shippingAddress: null,
    deliveryTiming: null,
    paymentMethod: null,
    shippingArea: null,
    selectedOrder: null,
};

export default (state = INITIAL_STATE, action) => {

    switch (action.type) {

        case SET_BILLING_ADDRESS:
            return { ...state, billingAddress: action.payload };

        case SET_SHIPPING_ADDRESS:
            return { ...state, shippingAddress: action.payload };

        case SET_SHIPPING_AREA:
            return { ...state, shippingArea: action.payload };

        case SET_DELIVERY_TIMING:
            return { ...state, deliveryTiming: action.payload };

        case SET_PAYMENT_METHOD:
            return { ...state, paymentMethod: action.payload };

        case SET_SELECTED_ORDER:
            return { ...state, selectedOrder: action.payload };

        default:
            return state;

    }
};
