import { SET_AUTH, SET_USER, SET_ROUTE_LINK } from '../types';

export const setUser = (payload: any) => {
    return (dispatch) => {
        dispatch({ type: SET_USER, payload });
    }
}

export const setAuth = (payload: boolean) => {
    return (dispatch) => {
        dispatch({ type: SET_AUTH, payload });
    }
}

export const setRouteLink = (payload: boolean) => {
    return (dispatch) => {
        dispatch({ type: SET_ROUTE_LINK, payload });
    }
}
