import React from 'react';
import { FlatList, Image, Text, TouchableOpacity, View } from 'react-native';
import { heightPercentageToDP as hp } from 'react-native-responsive-screen';
import { MowTitleView } from '../../../components/ui/MowTitleView';
import { mowColors } from '../../../values/Colors/MowColors';
import { categoryStyle } from '../../../values/Styles/MowStyles';

export const FeaturedDepartments = ({ navigation, data, onClick }): React.ReactElement => {

    return (
        <View
            style={[categoryStyle, { paddingBottom: hp(2), backgroundColor: mowColors.categoryBGColor }]}>
            <MowTitleView
                showButton={false}
                title='Featured Departments' />
            <FlatList
                horizontal={true}
                showsHorizontalScrollIndicator={false}
                keyExtractor={(item, index) => item.uuid}
                data={data}
                renderItem={({ item, index }) => (
                    <TouchableOpacity
                        onPress={() => { onClick(item) }}
                        style={
                            {
                                backgroundColor: mowColors.mainColor,
                                borderRadius: 10,
                                width: hp('12%'),
                                height: hp('12%'),
                                marginRight: 10,
                                justifyContent: 'center',
                                alignItems: 'center'
                            }
                        }
                        key={item.uuid}>
                        <Image
                            style={{ width: hp('7%'), height: hp('7%') }}
                            source={{ uri: item.thumb }}
                            resizeMode={'contain'} />
                        <Text
                            style={{
                                marginTop: 5,
                                fontSize: hp('1.4%'),
                                fontWeight: 'bold',
                                fontStyle: 'normal',
                                letterSpacing: 0,
                                textAlign: 'center',
                                color: '#ffffff',
                            }}>

                            {item.name}

                        </Text>

                    </TouchableOpacity>

                )}
            />

        </View>
    )
};
