import React from 'react';
import { FlatList, Text, TouchableOpacity, View, Picker } from 'react-native';
import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scroll-view';
import { heightPercentageToDP as hp, widthPercentageToDP as wp } from 'react-native-responsive-screen';
import { connect } from 'react-redux';
import { MowButtonBasic } from '../../components/ui/Common/Button/MowButton';
import { MowCheckBox } from '../../components/ui/Common/CheckBox/MowCheckBox';
import { MowInput } from '../../components/ui/Common/Input/MowInput';
import { MowModal } from '../../components/ui/Common/Modal/MowModal';
import { _showToast } from '../../components/ui/Common/Toast/MowToast';
import MowContainer from '../../components/ui/Core/Container/MowContainer';
import { MowInfoHeader } from '../../components/ui/MowInfoHeader';
import { CustomerAddressController } from '../../controller/customerAddress.controller';
import { AreaController } from '../../controller/area.controller';
import { mowColors } from '../../values/Colors/MowColors';
import { borderStyle, categoryStyleWithoutShadow, fontFamily, pageContainerStyle } from '../../values/Styles/MowStyles';
import { setShippingAddress, setShippingArea } from '../../store/actions/orderActions';

interface IProps {
    user: any;
    product: any;
    navigation: any;
    setShippingAddress(data): any;
    setShippingArea(data): any;
}

interface IState {
    addresses: any[];
    areas: any[];
    selectedAddress: any;
    selectedArea: any;
    isModalVisible: boolean;

    firstName: string;
    lastName: string;
    phoneNumber: string;
    city: string;
    zip: string;
    address: string;
    title: string;
}

class ShippingAddressScreen extends React.Component<IProps, IState> {

    modelController = new CustomerAddressController();
    areaController = new AreaController();
    _unsubscribe = null;

    iconColor = mowColors.mainColor;

    pickerStyle = {
        container: {
            marginVertical: 5,
        },
        button: {
            ...borderStyle,
            backgroundColor: mowColors.viewBGColor,
        },
        buttonText: {
            width: '63%',
            fontSize: hp('1.8%'),
            fontWeight: 'normal',
            fontStyle: 'normal',
            letterSpacing: 0,
            textAlign: 'left',
            fontFamily: fontFamily.regular,
            color: '#aeaeae',
        },
        buttonIcon: {
            color: mowColors.mainColor,
        },
    };

    inputStyle = {
        container: {
            backgroundColor: mowColors.viewBGColor,
        },
    };

    constructor(props) {
        super(props);

        this.state = {
            addresses: [],
            areas: [],
            selectedAddress: null,
            selectedArea: null,
            isModalVisible: false,
            firstName: null,
            lastName: null,
            phoneNumber: null,
            city: null,
            zip: null,
            address: null,
            title: null,
        };
    }

    onModalClose = () => {
        this.setState({ isModalVisible: false });
        this.getAddress();
    }

    saveAddress = () => {
        this.setState({ isModalVisible: false });
        const payload = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            phoneNumber: this.state.phoneNumber,
            city: this.state.city,
            zip: this.state.zip,
            address: this.state.address,
            title: this.state.title,
            type: 'Shipping',
            customer: this.props.user.user.uuid,
        }

        this.modelController.create(payload)
            .subscribe((result: any) => {
                const data = result.data;
                if (data.success) {
                    _showToast.success('Address Added');
                    this.getAddress();
                }
            })
    }

    getAddress = () => {
        this.modelController.getAll({ customer: this.props.user.user.uuid, type: 'Shipping' })
            .subscribe((result: any) => {
                const data = result.data;
                this.setState({ addresses: data.data });
            });
    }

    getAreas = () => {
        this.areaController.getAll({ take: 'all' })
            .subscribe((result: any) => {
                const data = result.data;
                this.setState({ areas: data.data });
            });
    }

    componentDidMount() {
        this._unsubscribe = this.props.navigation.addListener('focus', () => {
            this.getAreas();
            this.getAddress();
        });
    }

    componentWillUnmount() {
        this._unsubscribe();
    }

    onChangeText(name, value) {
        if (name === 'firstName') {
            this.setState({ firstName: value });
        }

        if (name === 'lastName') {
            this.setState({ lastName: value });
        }

        if (name === 'phoneNumber') {
            this.setState({ phoneNumber: value });
        }

        if (name === 'city') {
            this.setState({ city: value });
        }

        if (name === 'zip') {
            this.setState({ zip: value });
        }

        if (name === 'address') {
            this.setState({ address: value });
        }

        if (name === 'title') {
            this.setState({ title: value });
        }
    }

    onSelectAddress = (item) => {
        this.setState({ selectedAddress: item });
    }

    render() {

        return (

            <MowContainer
                navigation={this.props.navigation}
                style={{ backgroundColor: mowColors.pageBGDarkColor }}
                title={'Shipping Address'}>
                {
                    this.props.product.cart.length > 0 ?
                        <MowInfoHeader
                            activeIndex={2} /> : null
                }
                <View
                    style={{
                        width: wp('45%'),
                        marginLeft: 10,
                        marginTop: 10,
                    }}>
                    <Picker
                        selectedValue={this.state.selectedArea}
                        style={{ height: 50, width: 350 }}
                        onValueChange={(itemValue, itemIndex) => this.setState({ selectedArea: itemValue })}
                    >
                        {
                            this.state.areas.map(o => <Picker.Item label={`${o.area}, ${o.city}`} value={o} />)
                        }

                    </Picker>
                </View>
                <View
                    style={{
                        width: wp('45%'),
                        marginLeft: 10,
                        marginTop: 10,
                    }}>
                    <MowButtonBasic
                        containerStyle={{ marginVertical: 0 }}
                        onPress={() => { this.setState({ isModalVisible: true }) }}
                        size={'xSmall'}
                        stickyIcon={true}
                        leftIcon={'plus'}
                        type={'success'}>
                        ADD NEW
                    </MowButtonBasic>
                </View>
                {
                    this.state.addresses.length > 0 ?
                        <FlatList
                            showsVerticalScrollIndicator={false}
                            keyExtractor={(item, index) => index.toString()}
                            data={this.state.addresses}
                            style={{ marginTop: 10 }}
                            renderItem={({ item, index }) => (

                                <TouchableOpacity
                                    onPress={() => { this.onSelectAddress(item) }}
                                    key={index}
                                    style={[
                                        categoryStyleWithoutShadow, {
                                            margin: 10,
                                            marginVertical: 7,
                                            flexDirection: 'row',
                                            ...borderStyle,
                                            backgroundColor: mowColors.viewBGColor,
                                        }]}>

                                    <MowCheckBox
                                        checkedColor={mowColors.mainColor}
                                        checked={
                                            this.state.selectedAddress === null ?
                                                false : (this.state.selectedAddress.uuid === item.uuid)}
                                        containerStyle={{ marginRight: wp(5), alignSelf: 'center' }} />

                                    <View
                                        style={{ right: wp(2) }}>
                                        <Text
                                            style={{
                                                fontSize: hp('2%'),
                                                fontWeight: '500',
                                                fontStyle: 'normal',
                                                letterSpacing: -0.01,
                                                textAlign: 'left',
                                                color: mowColors.titleTextColor,
                                                width: '95%',
                                            }}>
                                            {item.title}
                                        </Text>
                                        <Text
                                            style={{
                                                marginVertical: 3,
                                                opacity: 0.72,
                                                fontSize: hp('1.8%'),
                                                fontWeight: '500',
                                                fontStyle: 'normal',
                                                lineHeight: 20,
                                                letterSpacing: 0,
                                                textAlign: 'left',
                                                color: mowColors.titleTextColor,
                                                width: '90%',
                                            }}>
                                            {item.address}
                                        </Text>
                                        <View
                                            style={{ flexDirection: 'row' }}>
                                            <Text
                                                style={{
                                                    fontSize: hp('1.7%'),
                                                    opacity: 0.72,
                                                    fontWeight: '500',
                                                    fontStyle: 'normal',
                                                    letterSpacing: 0,
                                                    textAlign: 'left',
                                                    color: mowColors.titleTextColor,
                                                }}>
                                                {item.firstName} {item.lastName}
                                            </Text>
                                        </View>
                                    </View>
                                </TouchableOpacity>
                            )}
                        /> : <View>
                            <Text>No Address Saved</Text>
                        </View>
                }
                {
                    this.state.selectedAddress !== null && this.state.selectedArea !== null ?
                        <View
                            style={{ margin: 10 }}>
                            <MowButtonBasic
                                onPress={() => {
                                    this.props.setShippingAddress(this.state.selectedAddress);
                                    this.props.setShippingArea(this.state.selectedArea);
                                    this.props.navigation.navigate('DeliveryTiming');
                                }}
                                type={'success'}>
                                Continue
                            </MowButtonBasic>
                        </View> : null
                }

                <MowModal
                    modalVisible={this.state.isModalVisible}
                    onClosed={this.onModalClose}
                    title={'Add New Shipping Address'}
                    closeButton={true}>

                    <KeyboardAwareScrollView
                        style={pageContainerStyle}
                        showsVerticalScrollIndicator={false}>

                        <MowInput
                            containerStyle={this.inputStyle.container}
                            onChangeText={value => this.onChangeText('firstName', value)}
                            placeholder={'First Name'}
                            iconColor={this.iconColor}
                            leftIcon={'user'}
                            type={'text'} />
                        <MowInput
                            containerStyle={this.inputStyle.container}
                            onChangeText={value => this.onChangeText('lastName', value)}
                            placeholder={'Last Name'}
                            iconColor={this.iconColor}
                            leftIcon={'user'}
                            type={'text'} />

                        {/* phone input */}
                        <MowInput
                            containerStyle={this.inputStyle.container}
                            onChangeText={value => this.onChangeText('phoneNumber', value)}
                            placeholder={'Phone Number'}
                            iconColor={this.iconColor}
                            leftIcon={'phone'}
                            type={'number'} />
                        <MowInput
                            containerStyle={this.inputStyle.container}
                            onChangeText={value => this.onChangeText('title', value)}
                            placeholder={'Address Title'}
                            iconColor={this.iconColor}
                            leftIcon={'navigation'}
                            type={'text'} />
                        <MowInput
                            containerStyle={this.inputStyle.container}
                            onChangeText={value => this.onChangeText('city', value)}
                            placeholder={'City'}
                            iconColor={this.iconColor}
                            leftIcon={'globe'}
                            type={'text'} />
                        <MowInput
                            containerStyle={this.inputStyle.container}
                            onChangeText={value => this.onChangeText('zip', value)}
                            placeholder={'Zip'}
                            iconColor={this.iconColor}
                            leftIcon={'globe'}
                            type={'text'} />

                        <MowInput
                            containerStyle={this.inputStyle.container}
                            onChangeText={value => this.onChangeText('address', value)}
                            placeholder={'Full Address'}
                            iconColor={this.iconColor}
                            leftIcon={'navigation'}
                            type={'textarea'} />

                    </KeyboardAwareScrollView>
                    <View
                        style={{ marginHorizontal: wp('3%') }}>
                        <MowButtonBasic
                            disabled={
                                this.state.firstName === null ||
                                this.state.lastName === null ||
                                this.state.city === null ||
                                this.state.zip === null ||
                                this.state.phoneNumber === null ||
                                this.state.title === null ||
                                this.state.address === null
                            }
                            onPress={this.saveAddress}
                            size={'medium'}
                            type={'success'}>
                            Save Address
                        </MowButtonBasic>
                    </View>
                </MowModal>
            </MowContainer>
        );
    }
}

const mapStateToProps = (state) => {
    const { user, product } = state;
    return { user, product };
};
const mapDispatchToProps = {
    setShippingAddress: setShippingAddress,
    setShippingArea: setShippingArea,
};
export default connect(mapStateToProps, mapDispatchToProps)(ShippingAddressScreen);
