import {
    SET_DEPARTMENT, SET_CATEGORY, SET_SUB_CATEGORY,
} from '../types';

const INITIAL_STATE = {
    department: null,
    category: null,
    subCategory: null,
};

export default (state = INITIAL_STATE, action) => {

    switch (action.type) {

        case SET_DEPARTMENT:
            return { ...state, department: action.payload };

        case SET_CATEGORY:
            return { ...state, category: action.payload };

        case SET_SUB_CATEGORY:
            return { ...state, subCategory: action.payload };

        default:
            return state;

    }
};
